import './App.css';
import Toolbar from './Components/Toolbar';
import Canvas from './Components/Canvas';
import { useRef } from 'react';

function App() {
  const strokeColor = useRef('#000000');
  const lineWidth = useRef(5);
  const canvasRef = useRef(null);
  let ctx;
  let canvas;

  const clearCanvas = () => {
    ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
  };

  const saveCanvas = () => {
    canvas = canvasRef.current;
    var image = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream");
    window.location.href=image; //save locally
  }

  function handleImage(e){
    const canvas = canvasRef.current;
    const ctx = canvasRef.current.getContext('2d');
    var reader = new FileReader();
    reader.onload = function(event){
        const img = new Image();
        img.onload = function(){
          const canvasOffsetX = canvas.offsetLeft;
          const canvasOffsetY = canvas.offsetTop;

          // const rect = canvas.getBoundingClientRect();
          // const offsetX = e.clientX - rect.left;
          // const offsetY = e.clientY - rect.top;
          canvas.style.height = 'auto';

          // Save the current canvas size
          const currentWidth = canvas.width;
          const currentHeight = canvas.height;

          canvas.width = img.width;
          canvas.height = img.height;
          ctx.drawImage(img, 0, 0);

          canvas.width = window.innerWidth - canvasOffsetX;
          canvas.height = window.innerHeight - canvasOffsetY;

          // Calculate the new pointer's position relative to the resized canvas
          // const newOffsetX = (offsetX / currentWidth) * img.width;
          // const newOffsetY = (offsetY / currentHeight) * img.height;

          // Move the painting start position to the new pointer's position
          ctx.beginPath();
          ctx.moveTo(canvasOffsetX, canvasOffsetY);
        }
        img.src = event.target.result;
    }

    reader.readAsDataURL(e.target.files[0]);     
  }

  return (
      <section class="container">
      <Toolbar strokeColor={strokeColor}
        lineWidth={lineWidth}
        clearCanvas={clearCanvas}
        handleImage={handleImage}
        saveCanvas={saveCanvas}/>
      <Canvas strokeColor={strokeColor} lineWidth={lineWidth} canvasRef = {canvasRef}/>
    </section>
  );
}

export default App;
