import '../CSS/toolbar.css';
import React from 'react';
import { useState, useRef, useEffect } from 'react';

function Toolbar( { strokeColor, lineWidth, clearCanvas, handleImage, saveCanvas } ) {
  const toolbarRef = useRef(null);

  const toolbar = toolbarRef.current;

  const handleClick = (e) => {
    if (e.target.id === 'clear') {
      clearCanvas();
    }
    if (e.target.id === 'save') {
      saveCanvas();
    }
  };

  const handleChange = (e) => {
    if(e.target.id === 'stroke') {
      strokeColor.current = e.target.value;
    }
    if(e.target.id === 'lineWidth') {
      lineWidth.current = e.target.value;
    }
    if (e.target.id === 'file') {
        handleImage(e);
      }
  };

  return (
    <>
      <div id="toolbar" ref={toolbarRef}>
        <h1>Draw!</h1>
        <label htmlFor="stroke">Stroke</label>
        <input id="stroke" name='stroke' type="color" onChange={handleChange}></input>
        <label htmlFor="lineWidth">Line Width</label>
        <input id="lineWidth" name='lineWidth' type="number" value={lineWidth.current} onChange={handleChange}></input>
        <button id="clear" onClick={handleClick}>Clear</button>
        <button id="save" onClick={handleClick}>Download</button>
        <label htmlFor="file" >Upload File:</label>
        <input type="file" id="file" name="file" accept="image/png, image/jpeg" onChange={handleChange} />
      </div>
    </>
  );
}

export default Toolbar;
