import '../CSS/canvas.css';
import React from 'react';
import { useRef, useEffect } from 'react';

function Canvas({ strokeColor, lineWidth }) {
  // const [lineWidth, setLineWidth] = useState(5);
  // const [strokeColor, setStrokeColor] = useState('#000000');
  const isPainting = React.useRef(false);
  const canvasRef = useRef(null);
  // const toolbarRef = useRef(null);

  const clearCanvas = () => {
    let ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
  };

  useEffect(() => {
    // const toolbar = toolbarRef.current;
    const canvas = canvasRef.current;
    ctx = canvas.getContext('2d');

    const canvasOffsetX = canvas.offsetLeft;
    const canvasOffsetY = canvas.offsetTop;

    canvas.width = window.innerWidth - canvasOffsetX;
    canvas.height = window.innerHeight - canvasOffsetY;

    let startX;
    let startY;

    function draw(e) {
      if(!isPainting.current) {
          return;
      }

      ctx.lineWidth = lineWidth;
      ctx.lineCap = 'round';
      ctx.strokeStyle = strokeColor;

      if (ctx.lineWidth !== lineWidth || ctx.strokeStyle !== strokeColor) {
        // If line width or stroke color has changed, update the context and move to the new position
        ctx.lineWidth = lineWidth;
        ctx.strokeStyle = strokeColor;
        ctx.moveTo(e.clientX - canvasOffsetX, e.clientY - canvasOffsetY);
    }

      ctx.lineTo(e.clientX - canvasOffsetX, e.clientY - canvasOffsetY);
      ctx.stroke();
    }

    // function handleImage(e){
    //   var reader = new FileReader();
    //   reader.onload = function(event){
    //       var img = new Image();
    //       img.onload = function(){
    //           canvas.width = img.width;
    //           canvas.height = img.height;
    //           ctx.drawImage(img,0,0);
    //       }
    //       img.src = event.target.result;
    //   }
    //   reader.readAsDataURL(e.target.files[0]);     
    // }

    const handleMouseDown = (e) => {
      isPainting.current = true;
      startX = e.clientX - canvasOffsetX;
      startY = e.clientY - canvasOffsetY;
      ctx.beginPath();
      ctx.moveTo(startX, startY);
    };

    const handleMouseUp = (e) => {
      isPainting.current = false;
      ctx.closePath();
    };

    canvas.addEventListener('mousedown', handleMouseDown);
    canvas.addEventListener('mouseup', handleMouseUp);
    canvas.addEventListener('mousemove', draw);


    // const handleClick = (e) => {
    //   if (e.target.id === 'clear') {
    //       ctx.clearRect(0, 0, canvas.width, canvas.height);
    //   }
    //   if (e.target.id === 'save') {
    //     var image = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream");
    //     window.location.href=image; //save locally
    //   }
    // };

    // const handleChange = (e) => {
    //   console.log('handleChange called');
    //   if(e.target.id === 'stroke') {
    //     console.log('stroke color changed:', e.target.value);
    //       ctx.strokeStyle = e.target.value;
    //       setStrokeColor(e.target.value);
    //   }
    //   if(e.target.id === 'lineWidth') {
    //     console.log('line width changed:', e.target.value);
    //       ctx.lineWidth = e.target.value;
    //       setLineWidth(e.target.value);
    //   }
    //   if (e.target.id === 'file') {
    //       handleImage(e);
    //     }
    // };

    // toolbar.addEventListener('click', handleClick);
    // toolbar.addEventListener('change', handleChange);

    return () => {
      canvas.removeEventListener('mousedown', handleMouseDown);
      canvas.removeEventListener('mouseup', handleMouseUp);
      canvas.removeEventListener('mousemove', draw);
      // toolbar.removeEventListener('click', handleClick);
      // toolbar.removeEventListener('change', handleChange);
    };
}, [lineWidth, strokeColor]);

  return (
    <div>
        <canvas id="drawingCanvas" useRef={canvasRef}></canvas>
    </div>
  );
}

export default Canvas;
