import '../CSS/toolbar.css';
import React from 'react';
import { useState, useRef } from 'React';

function Toolbar() {
  const [lineWidth, setWidth] = React.useState('5');
  // const [lines, setLines] = React.useState([]);
  const isPainting = React.useRef(false);
  const myref = useRef(null);
  const canvas = document.getElementById("drawingCanvas"); 
  const toolbar = document.getElementById('toolbar');
  const ctx = canvas.getContext("2d"); 

  const canvasOffsetX = canvas.offsetLeft;
  const canvasOffsetY = canvas.offsetTop;

  canvas.width = window.innerWidth - canvasOffsetX;
  canvas.height = window.innerHeight - canvasOffsetY;

  // let isPainting = false;
  // let lineWidth = 5;
  let startX;
  let startY;

  const draw = (e) => {
    if(!isPainting.current) {
        return;
    }

    ctx.lineWidth = lineWidth;
    ctx.lineCap = 'round';

    ctx.lineTo(e.clientX - canvasOffsetX, e.clientY - canvasOffsetY);
    ctx.stroke();
  }

  function handleImage(e){
    var reader = new FileReader();
    reader.onload = function(event){
        var img = new Image();
        img.onload = function(){
            canvas.width = img.width;
            canvas.height = img.height;
            ctx.drawImage(img,0,0);
        }
        img.src = event.target.result;
    }
    reader.readAsDataURL(e.target.files[0]);     
  }

  toolbar.addEventListener('click', e => {
    if (e.target.id === 'clear') {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
    if (e.target.id === 'save') {
      var image = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream");
      window.location.href=image; //save locally
    }
  });

  toolbar.addEventListener('change', e => {
    if(e.target.id === 'stroke') {
        ctx.strokeStyle = e.target.value;
    }

    if(e.target.id === 'lineWidth') {
        lineWidth = e.target.value;
    }

    if (e.target.id === 'file') {
        handleImage(e);
      }
  });

  canvas.addEventListener('mousedown', (e) => {
    isPainting = true;
    startX = e.clientX - canvasOffsetX;
    startY = e.clientY - canvasOffsetY;
    ctx.beginPath();
    ctx.moveTo(startX, startY);
  });

  canvas.addEventListener('mouseup', e => {
    isPainting = false;
    ctx.closePath();
  });

  canvas.addEventListener('mousemove', draw);

  return (
    <>
      <div id="toolbar">
        <h1>Draw!</h1>
        <label for="stroke">Stroke</label>
        <input id="stroke" name='stroke' type="color"></input>
        <label for="lineWidth">Line Width</label>
        <input id="lineWidth" name='lineWidth' type="number" value="5"></input>
        <button id="clear">Clear</button>
        <button id="save">Download</button>
        <label for="file">Upload File:</label>
        <input type="file" id="file" name="file" accept="image/png, image/jpeg" />
      </div>
      <div>
        <canvas id="drawingCanvas"></canvas>
      </div>
    </>
  );
}

export default Toolbar;
