import './App.css';
import Toolbar from './Components/Toolbar';
import Canvas from './Components/Canvas';
import { useRef } from 'react';

function App() {
  const strokeColor = useRef('#000000');
  const lineWidth = useRef(5);
  const canvasRef = useRef(null);
  let ctx;
  let canvas;

  const clearCanvas = () => {
    ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
  };

  const saveCanvas = () => {
    canvas = canvasRef.current;
    var image = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream");
    window.location.href=image; //save locally
  }

  function handleImage(e){
    console.log('handleimage');
    canvas = canvasRef.current;
    ctx = canvasRef.current.getContext('2d');
    var reader = new FileReader();
    reader.onload = function(event){
        var img = new Image();
        img.onload = function(){
          const rect = canvas.getBoundingClientRect();
          const offsetX = e.clientX - rect.left;
          const offsetY = e.clientY - rect.top;

          // Save the current canvas size
          const currentWidth = canvas.width;
          const currentHeight = canvas.height;

          canvas.width = img.width;
          canvas.height = img.height;
          ctx.drawImage(img, 0, 0);

          // Adjust the pointer position relative to the new canvas size
          const newPointerX = (pointerX / currentWidth) * img.width;
          const newPointerY = (pointerY / currentHeight) * img.height;

          // Update the pointer position to reflect the new canvas size
          ctx.beginPath();
          ctx.moveTo(newPointerX, newPointerY);
            }
            img.src = event.target.result;
    }

    reader.readAsDataURL(e.target.files[0]);     
  }

  return (
      <section class="container">
      <Toolbar strokeColor={strokeColor}
        lineWidth={lineWidth}
        clearCanvas={clearCanvas}
        handleImage={handleImage}
        saveCanvas={saveCanvas}/>
      <Canvas strokeColor={strokeColor} lineWidth={lineWidth} canvasRef = {canvasRef}/>
    </section>
  );
}

export default App;
