import '../CSS/toolbar.css';
import React from 'react';
import { useRef } from 'react';

function Toolbar( { strokeColor, lineWidth, clearCanvas, handleImage, saveCanvas } ) {
  const toolbarRef = useRef(null);

  return (
    <div id="toolbar" ref={toolbarRef}>
      <h1>Draw!</h1>
      <ul>
        <li>
          <label htmlFor="stroke">Stroke</label>
          <input id="stroke" name='stroke' type="color" appearance="none" border="none" height="100px" onChange={(e) => strokeColor.current = e.target.value}></input>
        </li>
        <li><label htmlFor="lineWidth">Line Width</label></li>
        <li><input id="lineWidth" name='lineWidth' type="number" defaultValue={5} onChange={(e) => lineWidth.current = e.target.value}></input></li>
        <li><button id="clear" onClick={clearCanvas}>Clear</button></li>
        <li><button id="save" onClick={saveCanvas}>Download</button></li>
        <li><label htmlFor="file" className="custom-file-upload">Upload File:</label></li>
        <li><input type="file" id="file" name="file" accept="image/png, image/jpeg" onChange={handleImage} /></li>
      </ul>
    </div>
  );
}

export default Toolbar;
